#!/usr/bin/python3import subprocessimport osdef normal(cmd):    subprocess.run([cmd], stdout=subprocess.PIPE, stderr=subprocess.PIPE, check=True, shell=True)def normal_verbose(cmd):    call = subprocess.run([cmd], check=True, shell=True)def check_program(tool):    try:        subprocess.call([tool, "--help"],stdout=subprocess.PIPE)    except OSError as e:        if e.errno == os.errno.ENOENT:            print(tool + " not found, installing")            if tool == "pip":                normal("sudo apt install -y python-pip")            elif tool == "blastn":                normal("sudo apt install -y ncbi-blast+")            else:                normal("sudo apt install -y " + tool)    else:         print(tool +" installed and working fine")def setup():    cwd = os.getcwd()    list = []    with open("depend.txt", 'r') as dependencies:        list.append(dependencies.readline().strip())    for program in list:        check_program(program)    normal("pip install biopython")setup()print("all good boss")