#!/usr/bin/python3import subprocessimport osdef bash_command(cmd):    subprocess.run([cmd],check=True,shell=True)def normal(cmd):    subprocess.run([cmd], stdout=subprocess.PIPE, stderr=subprocess.PIPE, check=True, shell=True)def normal_verbose(cmd):    call = subprocess.run([cmd], check=True, shell=True)def check_program(tool):    try:        subprocess.call([tool, "--help"],stdout=subprocess.PIPE)    except OSError as e:        if e.errno == os.errno.ENOENT:            print(tool + " not found, installing")    else:         print(tool +" installed and working fine")normal_verbose('ls')cwd = os.getcwd()check_program("clustalo")check_program("git")print(cwd)